import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Header = ({ isLoggedIn }) => {
  const navigate = useNavigate();

  const handleLogoClick = (e) => {
    e.preventDefault();
    if (isLoggedIn) {
      navigate('/posts'); // Dashboard page
    } else {
      navigate('/'); // Home page
    }
  };

  return (
    <header className="bg-gradient-to-r from-purple-600 to-indigo-600">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          <a 
            href="#"
            onClick={handleLogoClick}
            className="text-2xl font-bold text-white cursor-pointer"
          >
            Collab<span className="text-yellow-300">Sphere</span>
          </a>

          <nav className="hidden md:flex space-x-4">
            {isLoggedIn ? (
              <>
                <Link to="/posts" className="text-white hover:text-yellow-300">
                  Dashboard
                </Link>
                <Link to="/posts/create" className="text-white hover:text-yellow-300">
                  Create Project
                </Link>
                <Link to="/my-projects" className="text-white hover:text-yellow-300">
                  My Projects
                </Link>
                <Link to="/messages" className="text-white hover:text-yellow-300">
                  Messages
                </Link>
                <Link to="/profile" className="text-white hover:text-yellow-300">
                  Profile
                </Link>
              </>
            ) : (
              <>
                <Link to="/browse" className="text-white hover:text-yellow-300">
                  Browse Projects
                </Link>
                <Link to="/login" className="text-white hover:text-yellow-300">
                  Login
                </Link>
                <Link to="/signup" className="text-white hover:text-yellow-300">
                  Sign Up
                </Link>
                <Link to="/about" className="text-white hover:text-yellow-300">
                  About
                </Link>
              </>
            )}
          </nav>
        </div>
      </div>
    </header>
  );
};

// Helper hook to check authentication status
export const useAuth = () => {
  const [isLoggedIn, setIsLoggedIn] = React.useState(false);

  React.useEffect(() => {
    // Check for token in cookies
    const cookies = document.cookie.split(';');
    const hasToken = cookies.some(cookie => 
      cookie.trim().startsWith('token=')
    );
    setIsLoggedIn(hasToken);
  }, []);

  return isLoggedIn;
};

export default Header;
