import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const LoadingSpinner = () => (
  <div className="flex items-center justify-center min-h-screen">
    <div className="animate-spin rounded-full h-10 w-10 border-4 border-indigo-600 border-t-transparent"></div>
  </div>
);

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch('/api/posts', {
          credentials: 'include'
        });
        const data = await response.json();
        setPosts(data);
      } finally {
        setLoading(false);
      }
    };
    fetchPosts();
  }, []);

  if (loading) return <LoadingSpinner />;

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-gradient-to-r from-purple-600 to-indigo-600">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <Link to="/" className="text-2xl font-bold text-white">
              Collab<span className="text-yellow-300">Sphere</span>
            </Link>

            <nav className="hidden md:flex space-x-4">
              <Link to="/posts" className="text-white hover:text-yellow-300">
                Dashboard
              </Link>
              <Link to="/posts/create" className="text-white hover:text-yellow-300">
                Create Project
              </Link>
              <Link to="/my-projects" className="text-white hover:text-yellow-300">
                My Projects
              </Link>
              <Link to="/messages" className="text-white hover:text-yellow-300">
                Messages
              </Link>
              <Link to="/profile" className="text-white hover:text-yellow-300">
                Profile
              </Link>
            </nav>
          </div>
        </div>
      </header>

      <div className="max-w-4xl mx-auto px-4 py-8">
        <h1 className="text-2xl font-bold text-gray-900 mb-6">Recent Posts</h1>

        {posts.map((post) => (
          <div key={post._id} className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow mb-6">
            <div className="flex justify-between items-start">
              <div>
                <h2 className="text-xl font-bold text-gray-900">{post.title}</h2>
                <span className="inline-block px-2 py-1 text-sm rounded bg-gray-100 text-gray-600 mt-1">
                  {post.type}
                </span>
              </div>
            </div>
            <p className="text-gray-700 mt-3">{post.body}</p>
            <div className="text-right mt-4">
              <Link to={`/posts/${post._id}`} className="text-indigo-600 hover:text-indigo-800 font-medium">
                Read more â†’
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Posts;
