import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { GithubIcon, Globe, Clock, UserCircle2 } from 'lucide-react';
import LearnMoreButton from './LearnMoreButton';

const LoadingSpinner = () => (
  <div className="flex items-center justify-center min-h-screen">
    <div className="animate-spin rounded-full h-10 w-10 border-4 border-indigo-600 border-t-transparent"></div>
  </div>
);

const NavigatingLearnMoreButton = ({ postId }) => {
  const navigate = useNavigate();

  const handleClick = () => {
    navigate(`/posts/${postId}`);
  };

  return (
    <div onClick={handleClick}>
      <LearnMoreButton />
    </div>
  );
};

const getTypeDetails = (type) => {
  const types = {
    'showcase': { icon: 'ðŸŽ¨', color: 'bg-green-100 text-green-800', label: 'Project Showcase' },
    'seeking-contributors': { icon: 'ðŸ¤', color: 'bg-blue-100 text-blue-800', label: 'Seeking Contributors' },
    'looking-to-join': { icon: 'ðŸ”', color: 'bg-purple-100 text-purple-800', label: 'Looking to Join' },
    'in-development': { icon: 'ðŸš§', color: 'bg-yellow-100 text-yellow-800', label: 'In Development' },
    'hackathon': { icon: 'ðŸƒ', color: 'bg-red-100 text-red-800', label: 'Hackathon' },
    'learning-project': { icon: 'ðŸ“š', color: 'bg-indigo-100 text-indigo-800', label: 'Learning Project' },
    'open-source': { icon: 'ðŸŒŸ', color: 'bg-orange-100 text-orange-800', label: 'Open Source' }
  };
  return types[type] || { icon: 'ðŸ“‹', color: 'bg-gray-100 text-gray-800', label: type };
};

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch('/api/posts', {
          credentials: 'include'
        });
        const data = await response.json();
        setPosts(data);
      } finally {
        setLoading(false);
      }
    };
    fetchPosts();
  }, []);

  if (loading) return <LoadingSpinner />;

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-gradient-to-r from-purple-600 to-indigo-600">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <Link to="/" className="text-2xl font-bold text-white">
              Collab<span className="text-yellow-300">Sphere</span>
            </Link>

            <nav className="hidden md:flex space-x-4">
              <Link to="/posts" className="text-white hover:text-yellow-300">
                Dashboard
              </Link>
              <Link to="/posts/create" className="text-white hover:text-yellow-300">
                Create Project
              </Link>
              <Link to="/my-projects" className="text-white hover:text-yellow-300">
                My Projects
              </Link>
              <Link to="/messages" className="text-white hover:text-yellow-300">
                Messages
              </Link>
              <Link to="/profile" className="text-white hover:text-yellow-300">
                Profile
              </Link>
            </nav>
          </div>
        </div>
      </header>

      <div className="max-w-4xl mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold text-gray-900">Recent Posts</h1>
          <Link 
            to="/posts/create"
            className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700 transition-colors"
          >
            Create New Post
          </Link>
        </div>

        <div className="space-y-6">
          {posts.map((post) => {
            const typeDetails = getTypeDetails(post.type);

            return (
              <div key={post._id} className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow mb-6">
                <div className="flex justify-between items-start">
                  <div>
                    <h2 className="text-xl font-bold text-gray-900">{post.title}</h2>
                    <div className="flex items-center gap-2 mt-2">
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-sm font-medium ${typeDetails.color}`}>
                        {typeDetails.icon} {typeDetails.label}
                      </span>
                    </div>
                  </div>
                  <div className="flex items-center gap-2 text-sm text-gray-500">
                    <UserCircle2 className="w-4 h-4" />
                    <span>{post.user?.username || 'Anonymous'}</span>
                  </div>
                </div>

                <p className="text-gray-700 mt-3 mb-4">{post.body}</p>

                <div className="mt-4 pt-4 border-t border-gray-100">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-4">
                      {post.github && (
                        <a 
                          href={post.github}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="flex items-center gap-1 text-gray-600 hover:text-gray-900"
                        >
                          <GithubIcon className="w-4 h-4" />
                          <span className="text-sm">GitHub</span>
                        </a>
                      )}
                      {post.demo && (
                        <a 
                          href={post.demo}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="flex items-center gap-1 text-gray-600 hover:text-gray-900"
                        >
                          <Globe className="w-4 h-4" />
                          <span className="text-sm">Live Demo</span>
                        </a>
                      )}
                    </div>

                    <div className="flex justify-end">
                      <NavigatingLearnMoreButton postId={post._id} />
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Posts;
