  const [formData, setFormData] = useState({
    title: '',
    type: 'showcase',
    body: '',
    github: '',
    demo: ''
  });

  const [error, setError] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const postTypes = [
    { value: 'showcase', label: 'üé® Project Showcase - Share your completed project' },
    { value: 'seeking-contributors', label: 'ü§ù Seeking Contributors - Looking for team members' },
    { value: 'looking-to-join', label: 'üîç Looking to Join - Want to contribute to projects' },
    { value: 'in-development', label: 'üöß In Development - Project in progress, feedback welcome' },
    { value: 'hackathon', label: 'üèÉ Hackathon Project - Speed-built project' },
    { value: 'learning-project', label: 'üìö Learning Project - Educational project seeking feedback' },
    { value: 'open-source', label: 'üåü Open Source - Contributing to open source' }
  ];

  // ... keep all your existing handlers

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="bg-gradient-to-r from-purple-600 to-indigo-600">
        <Header />
      </div>

      <div className="max-w-2xl mx-auto px-4 py-8">
        <div className="bg-white rounded-lg shadow-md p-6">
          <h1 className="text-2xl font-bold text-gray-900 mb-6">Create New Post</h1>

          {error && (
            <div className="bg-red-50 text-red-500 p-4 rounded-md mb-6">
              {error}
            </div>
          )}

          {/* Keep your existing form JSX */}
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* ... rest of your form content stays exactly the same ... */}
          </form>
        </div>
      </div>
    </div>
  );
};

export default Create;
