import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import LearnMoreButton from './LearnMoreButton';
import Header from './components/Header';

const LoadingSpinner = () => (
  <div className="flex items-center justify-center min-h-screen">
    <div className="animate-spin rounded-full h-10 w-10 border-4 border-indigo-600 border-t-transparent"></div>
  </div>
);

const NavigatingLearnMoreButton = ({ postId }) => {
  const navigate = useNavigate();

  const handleClick = () => {
    navigate(`/posts/${postId}`);
  };

  return (
    <button
      onClick={handleClick}
      className="inline-flex items-center px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700"
    >
      Read More
      <svg className="ml-2 w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
      </svg>
    </button>
  );
};

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch('/api/posts', {
          credentials: 'include'
        });
        const data = await response.json();
        setPosts(data);
      } finally {
        setLoading(false);
      }
    };
    fetchPosts();
  }, []);

  if (loading) return <LoadingSpinner />;

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="bg-gradient-to-r from-purple-600 to-indigo-600">
        <Header />
      </div>

      <div className="max-w-4xl mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold text-gray-900">Recent Posts</h1>
          <Link 
            to="/posts/create"
            className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700 transition-colors"
          >
            Create New Post
          </Link>
        </div>

        {posts.map((post) => (
          <div key={post._id} className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow mb-6">
            <div className="flex justify-between items-start">
              <div>
                <h2 className="text-xl font-bold text-gray-900">{post.title}</h2>
                <span className="inline-block px-2 py-1 text-sm rounded bg-gray-100 text-gray-600 mt-1">
                  {post.type}
                </span>
              </div>
            </div>
            <p className="text-gray-700 mt-3 mb-4">{post.body}</p>

            {(post.github || post.demo) && (
              <div className="flex gap-4 mt-4 mb-4">
                {post.github && (
                  <a 
                    href={post.github}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-sm text-indigo-600 hover:text-indigo-800"
                  >
                    View on GitHub
                  </a>
                )}
                {post.demo && (
                  <a 
                    href={post.demo}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-sm text-indigo-600 hover:text-indigo-800"
                  >
                    View Demo
                  </a>
                )}
              </div>
            )}

            <div className="flex justify-end mt-4">
              <NavigatingLearnMoreButton postId={post._id} />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Posts;
