import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import Header from './components/Header';

const Signup = () => {
 const [formData, setFormData] = useState({
   firstName: '',
   lastName: '',
   Email: '',
   Password: '',
   confirmPassword: '',
 });

 const [errorMessage, setErrorMessage] = useState('');
 const [isLoading, setIsLoading] = useState(false);
 const navigate = useNavigate();

 const handleChange = (e) => {
   const { name, value } = e.target;
   setFormData({
     ...formData,
     [name]: value,
   });
 };

 const handleSubmit = async (e) => {
   e.preventDefault();
   setErrorMessage('');
   setIsLoading(true);

   if (formData.Password !== formData.confirmPassword) {
     setErrorMessage('Passwords do not match.');
     setIsLoading(false);
     return;
   }

   try {
     const response = await axios.post('/api/users/signup', formData);

     if (response.status === 200) {
       setFormData({
         firstName: '',
         lastName: '',
         Email: '',
         Password: '',
         confirmPassword: '',
       });
       setErrorMessage('Signup successful! Please check your email for the verification link.');
     }
   } catch (error) {
     if (error.response && error.response.data) {
       setErrorMessage(error.response.data.message);
     } else {
       setErrorMessage('Something went wrong. Please try again.');
     }
   } finally {
     setIsLoading(false);
   }
 };

 return (
   <div className="min-h-screen bg-gray-100">
     <div className="bg-gradient-to-r from-purple-600 to-indigo-600">
       <Header />
     </div>

     {/* Signup Form */}
     <div className="max-w-md mx-auto px-4 py-12">
       <div className="bg-white rounded-xl shadow-lg p-8">
         <h2 className="text-2xl font-bold text-gray-900 text-center mb-8">
           Create Your Account
         </h2>

         <form onSubmit={handleSubmit} className="space-y-6">
           <div className="grid grid-cols-2 gap-4">
             <div>
               <label htmlFor="firstName" className="block text-sm font-medium text-gray-700 mb-1">
                 First Name
               </label>
               <input
                 type="text"
                 id="firstName"
                 name="firstName"
                 value={formData.firstName}
                 onChange={handleChange}
                 required
                 className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
               />
             </div>

             <div>
               <label htmlFor="lastName" className="block text-sm font-medium text-gray-700 mb-1">
                 Last Name
               </label>
               <input
                 type="text"
                 id="lastName"
                 name="lastName"
                 value={formData.lastName}
                 onChange={handleChange}
                 required
                 className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
               />
             </div>
           </div>

           <div>
             <label htmlFor="Email" className="block text-sm font-medium text-gray-700 mb-1">
               Email Address
             </label>
             <input
               type="email"
               id="Email"
               name="Email"
               value={formData.Email}
               onChange={handleChange}
               required
               className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
             />
           </div>

           <div>
             <label htmlFor="Password" className="block text-sm font-medium text-gray-700 mb-1">
               Password
             </label>
             <input
               type="password"
               id="Password"
               name="Password"
               value={formData.Password}
               onChange={handleChange}
               required
               className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
             />
           </div>

           <div>
             <label htmlFor="confirmPassword" className="block text-sm font-medium text-gray-700 mb-1">
               Confirm Password
             </label>
             <input
               type="password"
               id="confirmPassword"
               name="confirmPassword"
               value={formData.confirmPassword}
               onChange={handleChange}
               required
               className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
             />
           </div>

           {errorMessage && (
             <div className={`text-sm ${errorMessage.includes('successful') ? 'text-green-600' : 'text-red-500'}`}>
               {errorMessage}
             </div>
           )}

           <button
             type="submit"
             disabled={isLoading}
             className="w-full bg-gradient-to-r from-purple-600 to-indigo-600 text-white py-2 px-4 rounded-md hover:from-purple-700 hover:to-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-colors duration-200 disabled:opacity-50"
           >
             {isLoading ? 'Creating Account...' : 'Create Account'}
           </button>
         </form>

         <div className="mt-6 text-center text-sm">
           <span className="text-gray-600">Already have an account?</span>
           <Link to="/login" className="ml-1 text-indigo-600 hover:text-indigo-500 font-medium">
             Sign in
           </Link>
         </div>
       </div>
     </div>
   </div>
 );
};

export default Signup;
