import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';

const VerifyEmail = () => {
  const { token } = useParams(); // Extract the token from the URL
  const navigate = useNavigate(); // For navigation
  const [isLoading, setIsLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  useEffect(() => {
    const verifyEmail = async () => {
      try {
        console.log('Token from useParams:', token); // Debugging
        const response = await axios.get(`/api/users/verify-email/${token}`);

        if (response.status === 200) {
          setIsLoading(false);
          setTimeout(() => {
            navigate('/login', { state: { verified: true } }); // Redirect with success message
          }, 3000);
        }
      } catch (err) {
        console.error('Error verifying email:', err);
        setError('Verification failed. Invalid or expired token.');
        setIsLoading(false);
      }
    };

    if (token) {
      verifyEmail(); // Only call if token exists
    } else {
      setError('Invalid request. No token provided.');
      setIsLoading(false);
    }
  }, [token, navigate]);

  // Loading state
  if (isLoading) {
    return (
      <div>
        <h2>Verifying Email...</h2>
        <p>Please wait while we verify your email.</p>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div>
        <h2>Verification Failed</h2>
        <p>{error}</p>
        <Link to="/signup">Signup Again</Link>
      </div>
    );
  }

  // Success state
  return (
    <div>
      <h2>Email Verified Successfully!</h2>
      <p>You will be redirected to the login page shortly.</p>
      <Link to="/login">Go to Login</Link>
    </div>
  );
};

export default VerifyEmail;
