import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const VerifyEmail = () => {
  const { token } = useParams();
  const navigate = useNavigate();
  const [message, setMessage] = useState('');
  const [isSuccess, setIsSuccess] = useState(false);

  useEffect(() => {
    const verifyEmail = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/api/users/verify-email/${token}`);
        if (response.status === 200) {
          setMessage('Email verified successfully! Redirecting to login...');
          setIsSuccess(true);

          // Redirect to login after 3 seconds
          setTimeout(() => navigate('/login'), 3000);
        }
      } catch (error) {
        console.log(error)
        setMessage('Invalid or expired token. Please try signing up again.');
        setIsSuccess(false);
      }
    };

    verifyEmail();
  }, [token, navigate]);

  return (
    <div>
      <h2>Email Verification</h2>
      <p>{message}</p>
      {isSuccess && (
        <p>
          You will be redirected to the <a href="/login">login page</a> shortly.
        </p>
      )}
    </div>
  );
};

export default VerifyEmail;
