import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const LoadingSpinner = () => (
  <div className="flex items-center justify-center min-h-screen">
    <div className="animate-spin rounded-full h-10 w-10 border-4 border-indigo-600 border-t-transparent"></div>
  </div>
);

const Profile = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
  });
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmNewPassword: '',
  });
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    fetchProfile();
  }, []);

// in your frontend Profile component
const fetchProfile = async () => {
  try {
    const response = await axios.get('/api/profile/me', {
      withCredentials: true
    });

    if (response.data && response.data.data) {
      setProfile(response.data.data);
      setFormData({
        firstName: response.data.data.firstName || '',
        lastName: response.data.data.lastName || '',
      });
    }
  } catch (error) {
    console.error('Failed to fetch profile:', error);
    setError('Failed to load profile');
    if (error.response?.status === 401) {
      navigate('/login');
    }
  } finally {
    setLoading(false);
  }
};

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    setError('');
    setSuccessMessage('');

    try {
      const response = await axios.put('/api/profile/me', formData, {
        withCredentials: true,
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (response.data && response.data.data) {
        setProfile(response.data.data);
        setSuccessMessage('Profile updated successfully');
        setIsEditing(false);
      }
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to update profile');
    }
  };

  const handlePasswordChange = async (e) => {
    e.preventDefault();
    setError('');
    setSuccessMessage('');

    if (passwordData.newPassword !== passwordData.confirmNewPassword) {
      setError('New passwords do not match');
      return;
    }

    try {
      await axios.put('/api/profile/change-password', passwordData, {
        withCredentials: true,
        headers: {
          'Content-Type': 'application/json'
        }
      });
      setSuccessMessage('Password updated successfully');
      setPasswordData({
        currentPassword: '',
        newPassword: '',
        confirmNewPassword: '',
      });
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to update password');
    }
  };

  if (loading) return <LoadingSpinner />;

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header remains the same */}
      <header className="bg-gradient-to-r from-purple-600 to-indigo-600">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <Link to="/" className="text-2xl font-bold text-white">
              Collab<span className="text-yellow-300">Sphere</span>
            </Link>

            <nav className="hidden md:flex space-x-4">
              <Link to="/posts" className="text-white hover:text-yellow-300">
                Dashboard
              </Link>
              <Link to="/posts/create" className="text-white hover:text-yellow-300">
                Create Project
              </Link>
              <Link to="/my-projects" className="text-white hover:text-yellow-300">
                My Projects
              </Link>
              <Link to="/messages" className="text-white hover:text-yellow-300">
                Messages
              </Link>
              <Link to="/profile" className="text-white hover:text-yellow-300">
                Profile
              </Link>
            </nav>
          </div>
        </div>
      </header>

      {/* Profile Content */}
      <div className="max-w-4xl mx-auto px-4 py-8">
        <div className="bg-white rounded-xl shadow-lg p-8">
          <h2 className="text-2xl font-bold text-gray-900 mb-6">My Profile</h2>

          {error && (
            <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded">
              {error}
            </div>
          )}

          {successMessage && (
            <div className="mb-4 p-3 bg-green-100 border border-green-400 text-green-700 rounded">
              {successMessage}
            </div>
          )}

          {profile ? (
            <>
              {/* Profile Information */}
              <div className="mb-8">
                {/* Rest of the component remains the same */}
                {/* ... */}
              </div>
            </>
          ) : (
            <div className="text-center text-gray-500">
              No profile information available
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
