import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';

const verifyEmail = async () => {
  try {
    console.log('Token from useParams:', token); // Debugging
    const response = await axios.get(`/api/users/verify-email/${token}`);

    if (response.status === 200) {
      setIsLoading(false);
      setTimeout(() => {
        navigate('/login', { state: { verified: true } }); // Pass success message
      }, 3000);
    }
  } catch (err) {
    console.error('Error verifying email:', err);
    setError('Verification failed. Invalid or expired token.');
    setIsLoading(false);
  }
};
