import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Signup = () => {
  const [formData, setFormData] = useState({
    firstName: '', // Ensure empty string, not undefined
    lastName: '',
    email: '', // Fix casing to lowercase to match the input name
    password: '', // Fix casing to lowercase to match the input name
    confirmPassword: '',
  });



  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormData({
      ...formData,
      [name]: value, // Dynamically update the correct field
    });
  };


  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage(''); // Clear previous errors

    console.log('Form Data:', formData); // Log form data for debugging
    try {
      const response = await axios.post('/api/users/signup', formData);

      if (response.status === 200) {
        // Redirect to login page after successful signup
        navigate('/login');
      }
    } catch (error) {
      if (error.response && error.response.data) {
        setErrorMessage(error.response.data.message); // Display backend error message
      } else {
        setErrorMessage('Something went wrong. Please try again.');
      }
    }
  };

  return (
    <div className="signup-container">
      <h2>Signup</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
        <input
          type="text"
          id="firstName"
          name="firstName" // Matches key in formData
          value={formData.firstName}
          onChange={handleChange}
          required
        />

        <input
          type="text"
          id="lastName"
          name="lastName" // Matches key in formData
          value={formData.lastName}
          onChange={handleChange}
          required
        />

        <input
          type="email"
          id="email"
          name="email" // Matches lowercase 'email' in formData
          value={formData.email}
          onChange={handleChange}
          required
        />

        <input
          type="password"
          id="password"
          name="password" // Matches lowercase 'password' in formData
          value={formData.password}
          onChange={handleChange}
          required
        />

        </div>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        <button type="submit">Signup</button>
      </form>
    </div>
  );
};

export default Signup;
