import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';

const VerifyEmail = () => {
  const { token } = useParams();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    console.log('Token from useParams:', token); // Debugging
    const verifyEmail = async () => {
      try {
        const response = await axios.get(`/api/users/verify-email/${token}`);
        console.log('Response:', response); // Debugging
        if (response.status === 200) {
          setIsLoading(false);
          setTimeout(() => {
            navigate('/login');
          }, 3000);
        }
      } catch (err) {
        console.error('Error verifying email:', err);
        setError('Invalid or expired token. Please try signing up again.');
        setIsLoading(false);
      }
    };
    if (token) {
      verifyEmail();
    } else {
      setError('Token is missing. Please check the link.');
      setIsLoading(false);
    }
  }, [token, navigate]);

  if (isLoading) {
    return (
      <div>
        <h2>Verifying Email...</h2>
        <p>Please wait while we verify your email.</p>
      </div>
    );
  }

  if (error) {
    return (
      <div>
        <h2>Verification Failed</h2>
        <p>{error}</p>
        <Link to="/signup" className="homepage-link">Signup Again</Link>
      </div>
    );
  }

  return (
    <div>
      <h2>Email Verified Successfully!</h2>
      <p>You will be redirected to the login page shortly.</p>
      <Link to="/login" className="homepage-link">Go to Login</Link>
    </div>
  );
};

export default VerifyEmail;
