import React, { useState, useEffect } from 'react';

const LoadingSpinner = () => (
  <div className="flex items-center justify-center min-h-screen">
    <div className="animate-spin rounded-full h-10 w-10 border-4 border-indigo-600 border-t-transparent"></div>
  </div>
);

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch('/api/posts', {
          credentials: 'include'
        });
        const data = await response.json();
        setPosts(data);
      } finally {
        setLoading(false);
      }
    };
    fetchPosts();
  }, []);

  if (loading) return <LoadingSpinner />;

  return (
    <div className="min-h-screen bg-gray-50">
      <nav className="bg-gray-800">
        <div className="mx-auto max-w-7xl px-4">
          <div className="relative flex h-16 items-center justify-between">
            <div className="flex flex-1 items-center justify-center sm:items-stretch sm:justify-start">
              <div className="flex flex-shrink-0 items-center">
                <a href="/" className="text-white text-xl font-bold">CollabSphere</a>
              </div>
              <div className="sm:ml-6 sm:block">
                <div className="flex space-x-4">
                  <a href="/" className="text-gray-300 hover:bg-gray-700 hover:text-white rounded-md px-3 py-2">
                    Home
                  </a>
                  <a href="/posts" className="bg-gray-900 text-white rounded-md px-3 py-2">
                    Posts
                  </a>
                  <a href="/projects" className="text-gray-300 hover:bg-gray-700 hover:text-white rounded-md px-3 py-2">
                    Projects
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </nav>

      <header className="bg-gradient-to-r from-purple-600 to-indigo-600 text-white py-4 shadow-md">
        <div className="max-w-4xl mx-auto px-4">
          <h1 className="text-2xl font-bold">Recent <span className="text-yellow-300">Posts</span></h1>
        </div>
      </header>

      <main className="max-w-4xl mx-auto px-4 py-8">
        {posts.map((post) => (
          <div key={post._id} className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow mb-6">
            <div className="flex justify-between items-start">
              <div>
                <h2 className="text-xl font-bold text-gray-900">{post.title}</h2>
                <span className="inline-block px-2 py-1 text-sm rounded bg-gray-100 text-gray-600 mt-1">
                  {post.type}
                </span>
              </div>
            </div>
            <p className="text-gray-700 mt-3">{post.body}</p>
            <div className="text-right mt-4">
              <a href={`/posts/${post._id}`} className="text-indigo-600 hover:text-indigo-800 font-medium">
                Read more â†’
              </a>
            </div>
          </div>
        ))}
      </main>
    </div>
  );
};

export default Posts;
