import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Header from './components/Header';

const LoadingSpinner = () => (
  <div className="flex items-center justify-center min-h-screen">
    <div className="animate-spin rounded-full h-10 w-10 border-4 border-indigo-600 border-t-transparent"></div>
  </div>
);

const PostDetail = () => {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPost = async () => {
      try {
        console.log('Fetching post details...');
        const response = await fetch(`/api/posts/auth/${id}`, {
          credentials: 'include'
        });
        const data = await response.json();
        setPost(data);
      } catch (err) {
        console.log('Post fetch error:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchPost();
  }, [id]);

  if (loading) return <LoadingSpinner />;
  if (error) return <div>Error: {error}</div>;
  if (!post) return <div>Post not found</div>;

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="bg-gradient-to-r from-purple-600 to-indigo-600">
        <Header />
      </div>

      <div className="max-w-4xl mx-auto px-4 py-8">
        <div className="bg-white rounded-lg shadow-md p-6">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">{post.title}</h1>
          <span className="inline-block px-2 py-1 text-sm rounded bg-gray-100 text-gray-600 mb-4">
            {post.type}
          </span>

          <p className="text-gray-700 mt-4 mb-6 whitespace-pre-wrap">{post.body}</p>

          {(post.github || post.demo) && (
            <div className="flex gap-4 mt-6">
              {post.github && (
                <a
                  href={post.github}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-indigo-600 hover:text-indigo-800"
                >
                  View on GitHub
                </a>
              )}
              {post.demo && (
                <a
                  href={post.demo}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-indigo-600 hover:text-indigo-800"
                >
                  View Demo
                </a>
              )}
            </div>
          )}

          {/* Add a section for comments here if you want */}
        </div>
      </div>
    </div>
  );
};

export default PostDetail;
